import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { environment } from '../../../environments/environment';
import { Product } from '../../models/product.model';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent implements OnInit {
  product: Product = {
    name: '',
    description: '',
    price: 0,
    category: 'men',
    subCategory: '',
    imageUrl: '',
    size: [],
    color: [],
    inStock: true,
    featured: false,
    rating: 0,
    numReviews: 0
  };

  categories = ['men', 'women'];
  subCategories: { [key: string]: string[] } = {
    men: ['shirts', 'pants', 'shoes', 'accessories'],
    women: ['dresses', 'tops', 'skirts', 'shoes', 'accessories']
  };

  constructor(private http: HttpClient, private router: Router) {}

  ngOnInit(): void {}

  onSubmit(): void {
    // Convert comma-separated strings to arrays
    this.product.size = this.product.size[0].split(',').map(s => s.trim());
    this.product.color = this.product.color[0].split(',').map(c => c.trim());

    this.http.post(`${environment.apiUrl}/products`, this.product)
      .subscribe({
        next: () => {
          this.router.navigate(['/products']);
        },
        error: (error) => {
          console.error('Error adding product:', error);
        }
      });
  }
}
