import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Product {
  name: string;
  description: string;
  price: number;
  category: 'men' | 'women';
  subCategory: string;
  imageUrl: string;
  size: string[];
  color: string[];
  inStock: boolean;
  featured: boolean;
  rating: number;
  numReviews: number;
}

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  product: Product = {
    name: '',
    description: '',
    price: 0,
    category: 'men',
    subCategory: '',
    imageUrl: '',
    size: [''],
    color: [''],
    inStock: true,
    featured: false,
    rating: 0,
    numReviews: 0
  };

  categories: ('men' | 'women')[] = ['men', 'women'];
  subCategories: Record<'men' | 'women', string[]> = {
    men: ['shirts', 'pants', 'hoodies', 'jackets', 'accessories'],
    women: ['dresses', 'jeans', 'blazers', 'tops', 'skirts']
  };

  constructor(private http: HttpClient) {}

  onSubmit() {
    // Split the comma-separated strings into arrays and trim whitespace
    const sizes = this.product.size[0].split(',').map(size => size.trim()).filter(size => size);
    const colors = this.product.color[0].split(',').map(color => color.trim()).filter(color => color);

    // Create the product object to submit
    const productToSubmit = {
      ...this.product,
      size: sizes,
      color: colors
    };

    console.log('Submitting product:', productToSubmit); // Debug log

    this.http.post('http://localhost:5001/api/products', productToSubmit)
      .subscribe({
        next: (response) => {
          console.log('Product added successfully:', response);
          alert('Product added successfully!');
          this.resetForm();
        },
        error: (error) => {
          console.error('Error adding product:', error);
          alert('Error adding product. Please try again.');
        }
      });
  }

  resetForm() {
    this.product = {
      name: '',
      description: '',
      price: 0,
      category: 'men',
      subCategory: '',
      imageUrl: '',
      size: [''],
      color: [''],
      inStock: true,
      featured: false,
      rating: 0,
      numReviews: 0
    };
  }
}
