import { Component } from '@angular/core';
import { Product } from '../../models/product.model';
import { ProductService } from '../../services/product.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  product: Partial<Product> = {
    name: '',
    description: '',
    price: 0,
    category: 'men',
    subCategory: '',
    imageUrl: '',
    size: [],
    color: [],
    inStock: true,
    featured: false,
    rating: 0,
    numReviews: 0
  };

  categories = ['men', 'women'];
  subCategories: { [key: string]: string[] } = {
    men: ['shirts', 'pants', 'shoes', 'accessories'],
    women: ['dresses', 'tops', 'skirts', 'shoes', 'accessories']
  };

  constructor(
    private productService: ProductService,
    private router: Router
  ) {}

  ngOnInit(): void {}

  onSubmit(): void {
    if (!this.product.size || !this.product.color) {
      return;
    }

    // Convert size and color arrays to JSON strings
    const productData = {
      ...this.product,
      size: JSON.stringify(this.product.size),
      color: JSON.stringify(this.product.color)
    };

    this.productService.createProduct(productData).subscribe({
      next: (response: Product) => {
        console.log('Product created successfully:', response);
        this.router.navigate(['/products']);
      },
      error: (error: any) => {
        console.error('Error creating product:', error);
      }
    });
  }
}
